import React, { Component } from "react";
import { connect } from 'react-redux';
import { createRoom } from '../../store/actions/roomActions';
import { Redirect } from 'react-router-dom';


//creates a new room 
class AddRoom extends Component {
  state = {
    title: "", 
    description: "",
    imageUrl: '',
    imageFile: null
  };

  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value,
    });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    //console.log(this.state);
    this.props.createRoom(this.state)
  };
  uploadRoomImage = () => {
    return (
      <div>
        <label htmlFor="imageFile">Upload Room Image</label>
        <input type="file" id=""onChange={this.handleChange}/>
      </div>
    );
  }
  render() {
    const { auth } = this.props;
    if (!auth.uid) return <Redirect to='/signin' />
    return (
      <div className="container">
        <form onSubmit={this.handleSubmit}>
          <h5 className="grey-text text-darken-3">Add New Room</h5>
          <div className="input-field">
            <label htmlFor="title">Title</label>
            <input type="text" id="title" onChange={this.handleChange} />
          </div>
          <div className="input-field">
            <label htmlFor="description">Room Description</label>
            <textarea
              type="text"
              id="description"
              className="materialize-textarea"
              onChange={this.handleChange}
            ></textarea>
          </div>
          <div className="input-field">
            <button className="btn red lighten-2 z-depth-0">Create</button>
          </div>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    auth: state.firebase.auth
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    createRoom: (room) => dispatch(createRoom(room))
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(AddRoom);
