export const createRoom = (room) => {
    return (dispatch, getState, { getFirebase, getFirestore }) => {
        //make async call to database
        const firestore = getFirestore();
        firestore.collection
        dispatch({ type: 'CREATE_ROOM', room: room })
        
    }
};
